import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../AuthContext';
import '../styles/styles.css';


const Navbar = () => {
  const { isAuthenticated, currentUser, logout } = useContext(AuthContext);

  const handleLogout = () => {
    logout();
  }

  const linkStyle = {
    marginRight: '10px', // Add some margin to the right of each link for spacing
    textDecoration: 'none', // Optionally, remove the underline from links
    color: 'blue' // Choose any color that fits your design
  };

  return (
    <nav>
      {isAuthenticated() ? (
        <>
          <Link to={`/user/${currentUser.userId}/monsters`} style={linkStyle}>My Monsters ({currentUser.username})</Link>
          <Link to="/create-monster" style={linkStyle}>Add Monster</Link>
          <Link to="/change-password" style={linkStyle}>Change Password</Link>
          <Link to="/delete-account" style={linkStyle}>Delete Account</Link>
          <button onClick={handleLogout} style={linkStyle}>Logout</button>
          <Link to="/users" style={linkStyle}>User List</Link>
        </>
      ) : (
        <>
          <Link to="/login" style={linkStyle}>Login</Link>
          <Link to="/register" style={linkStyle}>Register</Link>
          <Link to="/users" style={linkStyle}>User List</Link>
        </>
      )}
    </nav>
  );
}

export default Navbar;
