import React, { createContext, useState } from 'react';
import axios from 'axios';  // Ensure axios is imported to handle HTTP requests

axios.defaults.baseURL = 'http://localhost:5000';

const AuthContext = createContext();

const AuthProvider = ({ children }) => {
  const [currentUser, setCurrentUser] = useState(null);

  const isAuthenticated = () => {
    return currentUser !== null;
  };

  const login = async (username, password) => {
    try {
      const response = await axios.post('/api/users/login', {
        username,
        password
      });

      if (response.data && response.data.userId) {
        setCurrentUser({
          userId: response.data.userId,
          username: response.data.username
        });
      }
    } catch (error) {
      console.error('Error during login:', error);
      // Handle error feedback to user here
    }
  };


  const logout = () => {
    // Here you might want to send a logout request to the server if needed
    setCurrentUser(null);
  };

  return (
    <AuthContext.Provider value={{ currentUser, setCurrentUser, isAuthenticated, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
};

export { AuthContext, AuthProvider };
