import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../AuthContext';

const Navbar = () => {
  const { isAuthenticated, currentUser, logout } = useContext(AuthContext);

  const handleLogout = () => {
    logout();
  }

  const buttonStyle = {
    marginRight: '10px',
    padding: '5px 15px', // Padding makes the buttons larger and more button-like
    textDecoration: 'none',
    color: 'white', 
    backgroundColor: 'blue',
    border: '1px solid darkblue', 
    borderRadius: '5px', // Gives the button rounded corners
    cursor: 'pointer', // Changes the mouse cursor when hovering over the button
    transition: 'background-color 0.2s' // Smooth transition for button hover
  };

  // Add a hover effect to the buttons
  const buttonHoverStyle = {
    ...buttonStyle,
    backgroundColor: 'darkblue'
  };

  return (
    <nav>
      {isAuthenticated() ? (
        <>
          <Link to={`/user/${currentUser.userId}/monsters`} style={buttonStyle} onMouseOver={buttonHoverStyle}>My Monsters ({currentUser.username})</Link>
          <Link to="/create-monster" style={buttonStyle} onMouseOver={buttonHoverStyle}>Add Monster</Link>
          <Link to="/change-password" style={buttonStyle} onMouseOver={buttonHoverStyle}>Change Password</Link>
          <Link to="/delete-account" style={buttonStyle} onMouseOver={buttonHoverStyle}>Delete Account</Link>
          <Link to="#" onClick={handleLogout} style={buttonStyle} onMouseOver={buttonHoverStyle}>Logout</Link>
          <Link to="/users" style={buttonStyle} onMouseOver={buttonHoverStyle}>User List</Link>
        </>
      ) : (
        <>
          <Link to="/login" style={buttonStyle} onMouseOver={buttonHoverStyle}>Login</Link>
          <Link to="/register" style={buttonStyle} onMouseOver={buttonHoverStyle}>Register</Link>
          <Link to="/users" style={buttonStyle} onMouseOver={buttonHoverStyle}>User List</Link>
        </>
      )}
    </nav>
  );
}

export default Navbar;
